    "parent_id" TEXT,
    "tags" TEXT,
    "attachments" TEXT,
    "editors" TEXT,
    "version" INTEGER DEFAULT 1,
    "created_by" TEXT NOT NULL,
    "created_at" INTEGER NOT NULL,
    "updated_at" INTEGER,
    "updated_by" TEXT,
    "folder_id" TEXT
);

